  width: 98% !important;
}

.customily-modal-container .option_name {
  color: #333333 !important;
}

#customily-cart-btn.invalid-button,
#customily-personalize-button.invalid-button {
cursor: not-allowed !important;
opacity: 0.5 !important;
pointer-events: none !important;
}

.customily-error-handler span {
font-size: 12px !important;
line-height: normal !important;
color: red !important;
}

@media screen and (max-width: 767px) {
.customily-error-handler {margin-bottom: 0 !important;}
}

.customily-personalize-button {
  width: 100% !important;
}

.customily-close-button {
